var comp = app.project.activeItem;
if (!(comp instanceof CompItem)) {
    return 'No active composition. Please select a composition first.';
}

app.beginUndoGroup('My Script');
try {
    // Your script code here
} catch (e) {
    return 'Error: ' + e.toString();
} finally {
    app.endUndoGroup();
}

var comp = app.project.activeItem;
var solid = comp.layers.addSolid(
    [1, 0, 0],       // color [R,G,B] 0-1
    'Red Solid',      // name
    comp.width,       // width
    comp.height,      // height
    1,                // pixel aspect
    comp.duration     // duration
);

var comp = app.project.activeItem;
var textLayer = comp.layers.addText('Hello World');
var textProp = textLayer.property('ADBE Text Properties').property('ADBE Text Document');
var textDoc = textProp.value;
textDoc.fontSize = 72;
textDoc.font = 'Arial-BoldMT';
textDoc.fillColor = [1, 1, 1];
textDoc.justification = ParagraphJustification.CENTER_JUSTIFY;
textProp.setValue(textDoc);

var comp = app.project.activeItem;
var layer = comp.layer(1);
var pos = layer.property('Position');
pos.setValueAtTime(0, [100, 540]);
pos.setValueAtTime(2, [1820, 540]);

var comp = app.project.activeItem;
var layer = comp.layer(1);
var pos = layer.property('Position');
pos.setValueAtTime(0, [100, 540]);
pos.setValueAtTime(1, [960, 540]);
// Apply ease
var easeIn = new KeyframeEase(0, 75);
var easeOut = new KeyframeEase(0, 75);
pos.setTemporalEaseAtKey(1, [easeIn, easeIn]);
pos.setTemporalEaseAtKey(2, [easeOut, easeOut]);

var comp = app.project.activeItem;
var layer = comp.layer(1);
var effects = layer.property('ADBE Effect Parade');
var blur = effects.addProperty('ADBE Gaussian Blur 2');
blur.property('ADBE Gaussian Blur 2-0001').setValue(10); // Blurriness

var comp = app.project.activeItem;
for (var i = 1; i <= comp.numLayers; i++) {
    var layer = comp.layer(i);
    // Do something with each layer
}

var comp = app.project.activeItem;
var layer = comp.layer(1);
var opacity = layer.property('Opacity');
opacity.expression = 'wiggle(2, 20)';

var comp = app.project.activeItem;
var nullLayer = comp.layers.addNull();
nullLayer.name = 'Controller';
var targetLayer = comp.layer(2); // layer below null
targetLayer.parent = nullLayer;

var importFile = new ImportOptions(new File('/path/to/file.mov'));
if (importFile.canImportAs(ImportAsType.FOOTAGE)) {
    importFile.importAs = ImportAsType.FOOTAGE;
    var footage = app.project.importFile(importFile);
}

var comp = app.project.activeItem;
var layer = comp.layer(1);
layer.inPoint = comp.workAreaStart;
layer.outPoint = comp.workAreaStart + comp.workAreaDuration;

var comp = app.project.activeItem;
var layer = comp.layer(1);
var rect = layer.sourceRectAtTime(comp.time, false);
var anchorX = rect.left + rect.width / 2;
var anchorY = rect.top + rect.height / 2;
layer.property('Anchor Point').setValue([anchorX, anchorY]);
layer.property('Position').setValue([comp.width / 2, comp.height / 2]);

var comp = app.project.activeItem;
var shapeLayer = comp.layers.addShape();
var contents = shapeLayer.property('Contents');
var rectGroup = contents.addProperty('ADBE Vector Group');
var rect = rectGroup.property('Contents').addProperty('ADBE Vector Shape - Rect');
rect.property('ADBE Vector Rect Size').setValue([200, 200]);
var fill = rectGroup.property('Contents').addProperty('ADBE Vector Graphic - Fill');
fill.property('ADBE Vector Fill Color').setValue([0, 0.5, 1, 1]);

var comp = app.project.activeItem;
var layer = comp.layer(1);
var opacity = layer.property('Opacity');
opacity.setValueAtTime(0, 0);
opacity.setValueAtTime(1, 100);

var comp = app.project.activeItem;
var layer = comp.layer(1);
var scale = layer.property('Scale');
scale.setValueAtTime(0, [0, 0]);
scale.setValueAtTime(0.5, [100, 100]);
scale.expression = 'n = 0;
if (numKeys > 0) {
  n = nearestKey(time).index;
  if (key(n).time > time) n--;
}
if (n > 0) {
  t = time - key(n).time;
  amp = 0.06;
  freq = 3;
  decay = 5;
  value + value * amp * Math.sin(freq * t * 2 * Math.PI) / Math.exp(decay * t);
} else {
  value;
}';

var comp = app.project.activeItem;
var layer = comp.layer(1);
var copies = 5;
for (var i = 0; i < copies; i++) {
    var dup = layer.duplicate();
    dup.startTime = layer.startTime + (i + 1) * 0.1;
}

var comp = app.project.activeItem;
var layer = comp.layer(1);
var transform = layer.property('ADBE Transform Group');
var position = transform.property('ADBE Position');
var scale = transform.property('ADBE Scale');
var rotation = transform.property('ADBE Rotate Z');
var opacity = transform.property('ADBE Opacity');
var anchorPoint = transform.property('ADBE Anchor Point');

var comp = app.project.activeItem;
var selected = comp.selectedLayers;
if (selected.length > 0) {
    var indices = [];
    for (var i = 0; i < selected.length; i++) {
        indices.push(selected[i].index);
    }
    comp.layers.precompose(indices, 'Pre-comp', true);
}

var comp = app.project.activeItem;
var layer = comp.layer(1);
var marker = layer.property('Marker');
var markerVal = new MarkerValue('Hit Point');
markerVal.duration = 0.5;
marker.setValueAtTime(1.0, markerVal);

var comp = app.project.activeItem;
var layer = null;
for (var i = 1; i <= comp.numLayers; i++) {
    if (comp.layer(i).name === 'My Layer') {
        layer = comp.layer(i);
        break;
    }
}

var comp = app.project.activeItem;
var layer = comp.layer(1);
var pos = layer.property('Position');
pos.setValueAtTime(0, [100, 500]);
pos.setValueAtTime(1, [500, 200]);
pos.setValueAtTime(2, [900, 500]);
// Set spatial tangents for curved path
pos.setSpatialTangentsAtKey(1, [0, 0], [0, 0]);
pos.setSpatialTangentsAtKey(2, [-100, -100], [100, -100]);
pos.setSpatialTangentsAtKey(3, [0, 0], [0, 0]);

var comp = app.project.activeItem;
var layer = comp.layer(1);
var opacity = layer.property('Opacity');
opacity.setValueAtTime(0, 100);
opacity.setValueAtTime(1, 0);
opacity.setInterpolationTypeAtKey(1, KeyframeInterpolationType.HOLD);

var comp = app.project.activeItem;
var adj = comp.layers.addSolid([0,0,0], 'Adjustment', comp.width, comp.height, 1, comp.duration);
adj.adjustmentLayer = true;
var effects = adj.property('ADBE Effect Parade');
var cc = effects.addProperty('ADBE CurvesCustom'); // Curves

var comp = app.project.activeItem;
var offset = 0.1; // seconds between each layer
for (var i = 1; i <= comp.numLayers; i++) {
    comp.layer(i).startTime = (i - 1) * offset;
}

var comp = app.project.items.addComp(
    'My Comp',  // name
    1920,       // width
    1080,       // height
    1,          // pixel aspect
    10,         // duration seconds
    30          // frame rate
);
